DROP TABLE IF EXISTS `emotions`;

CREATE TABLE emotions (
  id int unsigned NOT NULL AUTO_INCREMENT COMMENT 'Идентификатор сроки',
  table_name varchar(255) NOT NULL COMMENT 'Название таблицы (сущности)',
  object_id int NOT NULL COMMENT 'Идентификатор строки (объекта) таблицы (сущности)',
  like_count int DEFAULT 0 COMMENT 'Количество лайков',
  dislike_count int DEFAULT 0 COMMENT 'Количество дизлайков', 
  created_at datetime DEFAULT CURRENT_TIMESTAMP COMMENT 'Время создания строки',
  updated_at datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Время обновления строки',
  PRIMARY KEY (`id`)
) COMMENT='Эмоции';

DESC emotions;

